$(document).ready(doOnLoad);

function doOnLoad() {
	console.log('Start function doOnLoad');
	init();
	console.log('Bloodhound');
	var g = new Bloodhound({
	  initialize: true,
	  local: genres,
	  queryTokenizer: Bloodhound.tokenizers.whitespace,
	  datumTokenizer: Bloodhound.tokenizers.whitespace
	});

	//g.initialize();
	console.log('tagsinput');
	$('#genre').tagsinput({
		trimValue: true,
		tagClass: 'label label-default',
		typeaheadjs: {
			name: 'genres',
			source: g.ttAdapter()
		},
		confirmKeys: [13, 44, 59]
	});
	$('#genre').on('beforeItemAdd', function(event) {
		var rgx = /(^[a-z])|( [a-z])|(-[a-z])/g;
		if(event.item.match(rgx)) {
				event.cancel = true;
			$('#genre').tagsinput('add', event.item.replace(rgx, function myFunction(x){return x.toUpperCase();}));
		}
	});

	$('#genre-form-group .tt-input').blur(function(event) {
		if($('#genre').val() == "") {
			$('#genre-form-group').removeClass("has-success");
			$('#genre-form-group .form-control-feedback').removeClass("glyphicon-ok");
		}
	});

	console.log('datepicker');
	$('#date .input-group.date').datepicker({
	    format: "dd/mm/yyyy",
	    autoclose: true,
	    toggleActive: true,
			startDate: '+1d'
	});
	console.log('validator');
	$('#eventForm').validator();
	if($('#validate').length > 0) {
		console.log('validate now');
		$('#eventForm').validator('validate');
	}
	if($('#modal #eventForm').length > 0) {
		console.log('On submit after validator');
		$("#eventForm").validator().on("submit", function(e) {
			console.log('Submit Event %s', $('#eventForm').attr('action'));
			if (!e.isDefaultPrevented()) {
				$.ajax({
					type:'POST',
					url: $('#eventForm').attr('action')+'/ajax',
					data: $('#eventForm').serialize(),
					success: function(responseText) {
						$('#modal .modal-body').html(responseText);
						if(responseText.indexOf('Successfully') >=0 || responseText.indexOf('Submitted') >= 0) {
							$('#modal .modal-footer').html('<button class="btn" data-dismiss="modal" aria-hidden="true">Close</button>');
						} else {
						$('#modal .modal-body .form-actions').css('display', 'none');
						doFormstuffOnLoad();
						doOnLoad();
					}
					},
					beforeSend: function() {
						$("#modal footer .btn-primary").attr('disabled','disabled');
						$('#modal .modal-body').html('<img src="'+baseurl+'/img/waiting.gif" />');
					},
					error: function() {
						alert('Submitting the form failed. Please check your internet connection and retry.');
						$("#modal footer .btn-primary").removeAttr('disabled');
					}
				});
			}
			return false;
		});
	}

	console.log('Loading venues array');
	// load venues from html
	var venues = [];
	$('#venues venue').each( function() {
		v = $(this);
		venues.push(
			{
				id: v.find('id').html(),
				name: v.find('name').html(),
				latitude: v.find('latitude').html(),
				longitude: v.find('longitude').html(),
				website: v.find('website').html(),
				zoom: v.find('zoom').html(),
				address: v.find('address').html()
			}
		);
	});

	console.log('Setting knowVenues onChange function');

	$('#knownVenues').change( function() {

		$.each(venues, function(i, venue) {
			//console.log('%o', venue);
			if(venue.id == $('#knownVenues').val()) {
				$('#venueName').val(venue.name);
				$('#venueAddress').val(venue.address);
				$('#venueLatitude').val(venue.latitude);
				$('#venueLongitude').val(venue.longitude);
				$('#venueWebsite').val(venue.website);
				$('#venueZoom').val(venue.zoom);

				MapTool.setMarker([venue.latitude, venue.longitude]);
				MapTool.map.setView([venue.latitude, venue.longitude], venue.zoom, {animate: true});

			}
		});
	});

	console.log('Setting date onBlur function');

	$('date').blur( function() {

		var today = new Date();
		var party = new Date($('date').val());
		if(today.diff(party)<0) {
			$('date').setCustomValidity('The party needs to be in the future!');
		} else {
			// input is valid -- reset the error message
			$('date').setCustomValidity('');
		}

	});

	console.log('Setting marker + map view if #venueLatitude and #venueLongitude are defined');

	if($('#venueLatitude').val() != '' && $('#venueLongitude').val() !='' && $('#venueZoom').val() != '' && MapTool != null) {
		console.log('%o', MapTool);
		console.log('%s %s',$('#venueLatitude').val(), $('#venueLongitude').val());
		MapTool.setMarker([$('#venueLatitude').val(), $('#venueLongitude').val()]);
		MapTool.map.setView([$('#venueLatitude').val(), $('#venueLongitude').val()], $('#venueZoom').val(), {animate: false});
	}

	console.log('Set searchAddress onClick function');
	$('#searchAddress').click( function() {
		console.log('Search Address: '+$('#venueAddress').val());
		$.getJSON('https://nominatim.openstreetmap.org/search?format=json&q='+$('#venueAddress').val(), function(r){
			if(r.length == 0)
				alert('Adress not found.');
			else {
				if(MapTool != null) {
					MapTool.setMarker([r[0].lat, r[0].lon]);
					MapTool.map.setView([r[0].lat, r[0].lon], 16, {animate: true});
				}

				$('#venueLatitude').val(r[0].lat);
				$('#venueLongitude').val(r[0].lon);
				$('#venueZoom').val(16);
			}
		});
		return false;
	});
}
